function t = timeultimate(x1, y1, a, b, m, a2, a3, a4, v)
t = integral(@(x) 2.*(1+(1/(x1)^3.*x.*(m.*(3.*x-2*x1).*x1+(x-x1).*((x1)^3.*(a2.*(4.*x-2*x1)+(x-x1).*(x.*(5.*a3+6.*a4.*x)-2.*(a3+4.*a4.*x).*x1+2.*a4.*(x1)^2))-6*y1))).^2).^(1/2)./(a.*((1+(1/(x1)^3.*x.*(m.*(3.*x-2*x1).*x1+(x-x1).*((x1)^3.*(a2.*(4.*x-2*x1)+(x-x1).*(x.*(5.*a3+6.*a4.*x)-2.*(a3+4.*a4.*x).*x1+2.*a4.*(x1)^2))-6*y1))).^2).^(3/2)./(((1./((x1)^3)).*2.*(m.*(3.*x-x1).*x1+(x1)^3.*(a2.*(6.*x.^2-6.*x.*x1+(x1).^2)+(x-x1).*(a4.*(x-x1).*(15.*x.^2-10.*x.*x1+(x1).^2)+a3.*(10.*x.^2-8.*x.*x1+(x1)^2)))+3.*(-2.*x+x1).*y1)).^2).^(1/2)-b).^(1/2)+v-((a.*((1+(1/(x1)^3.*x.*(m.*(3.*x-2*x1).*x1+(x-x1).*((x1)^3.*(a2.*(4.*x-2*x1)+(x-x1).*(x.*(5.*a3+6.*a4.*x)-2.*(a3+4.*a4.*x).*x1+2.*a4.*(x1)^2))-6*y1))).^2).^(3/2)./(((1./((x1)^3)).*2.*(m.*(3.*x-x1).*x1+(x1)^3.*(a2.*(6.*x.^2-6.*x.*x1+(x1).^2)+(x-x1).*(a4.*(x-x1).*(15.*x.^2-10.*x.*x1+(x1).^2)+a3.*(10.*x.^2-8.*x.*x1+(x1)^2)))+3.*(-2.*x+x1).*y1)).^2).^(1/2)-b).^(1/2)-v).^2).^(1/2)),0,x1);
end

% 드디어 좌표/방향 주어진 두점 이어주는 모델의 핵심 함수. 6차식 데이터 넣으면 시간 구해줌. 루프 내에서 사용됨.